// FormComponent.tsx
import React from 'react';
import { Form, Input, Button, Checkbox, Switch, Radio, Select } from 'antd';
import { FormInstance } from 'antd/lib/form';

interface FormComponentProps {
  onSubmit: (values: any) => void;
}

const inputFields = [
  { name: 'username', label: 'Username', type: 'text', placeholder: 'Enter your username' },
  { name: 'password', label: 'Password', type: 'password', placeholder: 'Enter your password' },
  { name: 'textLabel', label: 'Input with Text Label', type: 'text', placeholder: 'Enter text' }
];

class FormComponent extends React.Component<FormComponentProps> {
  formRef = React.createRef<FormInstance>();

  onFinish = (values: any) => {
    this.props.onSubmit(values);
  };

  render() {
    return (
      <Form
        ref={this.formRef}
        name="basic"
        onFinish={this.onFinish}
        labelCol={{ span: 8 }}
        wrapperCol={{ span: 16 }}
      >
        {inputFields.map((field) => (
          <Form.Item
            key={field.name}
            label={field.label}
            name={field.name}
            rules={[{ required: true, message: `Please input your ${field.label}!` }]}
          >
            <Input
              type={field.type}
              placeholder={field.placeholder}
            />
          </Form.Item>
        ))}

        <Form.Item
          name="rememberMe"
          valuePropName="checked"
          wrapperCol={{ offset: 8, span: 16 }}
        >
          <Checkbox>Remember me</Checkbox>
        </Form.Item>

        <Form.Item
          label="Switch"
          name="switch"
          valuePropName="checked"
        >
          <Switch />
        </Form.Item>

        <Form.Item
          label="Radio Button"
          name="radio"
        >
          <Radio.Group>
            <Radio value={1}>Option 1</Radio>
            <Radio value={2}>Option 2</Radio>
            <Radio value={3}>Option 3</Radio>
          </Radio.Group>
        </Form.Item>

        <Form.Item
          label="Dropdown List"
          name="dropdown"
          rules={[{ required: true, message: 'Please select!' }]}
        >
          <Select>
            <Select.Option value="option1">Option 1</Select.Option>
            <Select.Option value="option2">Option 2</Select.Option>
            <Select.Option value="option3">Option 3</Select.Option>
          </Select>
        </Form.Item>

        <Form.Item>
          <Button type="primary" htmlType="submit">
            Submit
          </Button>
        </Form.Item>
      </Form>
    );
  }
}

export default FormComponent;
