import React, { useState } from 'react';
import { Form, Input, Checkbox, Switch, Radio, Button, Dropdown, Menu } from 'antd';
import { UserOutlined, LockOutlined } from '@ant-design/icons';

const { Password } = Input;
const { Group: RadioGroup } = Radio;

const MyForm: React.FC = () => {
  const [rememberMe, setRememberMe] = useState(false);
  const [switchValue, setSwitchValue] = useState(false);
  const [radioValue, setRadioValue] = useState(1);

  const handleSwitchChange = (checked: boolean) => {
    setSwitchValue(checked);
  };

  const handleRadioChange = (e: any) => {
    setRadioValue(e.target.value);
  };

  const handleCancel = () => {
    // 
  };

  const handleNext = () => {
    // 
  };

  return (
    <Form>
      <Form.Item name="username">
        <Input prefix={<UserOutlined />} placeholder="Username" />
      </Form.Item>
      <Form.Item name="password">
        <Password prefix={<LockOutlined />} placeholder="Password" />
      </Form.Item>
      <Form.Item name="text">
        <Input placeholder="Text" />
      </Form.Item>
      <Form.Item>
        <Checkbox checked={rememberMe} onChange={e => setRememberMe(e.target.checked)}>
          Remember me
        </Checkbox>
      </Form.Item>
      <Form.Item>
        <Switch checked={switchValue} onChange={handleSwitchChange} />
      </Form.Item>
      <Form.Item>
        <RadioGroup onChange={handleRadioChange} value={radioValue}>
          <Radio value={1}>Button 1</Radio>
          <Radio value={2}>Button 2</Radio>
          <Radio value={3}>Button 3</Radio>
        </RadioGroup>
      </Form.Item>
      <Form.Item>

      </Form.Item>
      <Form.Item>
        <Button type="primary" onClick={handleNext}>
          Next
        </Button>
        <Button onClick={handleCancel}>Cancel</Button>
      </Form.Item>
    </Form>
  );
};

export default MyForm;
