import React, { useEffect, useState } from "react";
import { Button, Table } from "antd";
import { sendGet } from "../api";
// import { useNavigate } from 'react-router-dom';

const AllUser = () => {
    const [dataSource, setDataSource] = useState([]);
    //   const history = useNavigate(); // Khởi tạo useHistory

    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await sendGet('employees');
                setDataSource(data);
            } catch (error) {
                // Exeption
            }
        };

        fetchData();
    }, []);

    const fetchData = async () => {
        // Lấy dữ liệu từ API hoặc từ nguồn khác
    };

    const handleAddClick = () => {
        // Chuyển hướng sang trang thêm người dùng khi nhấn vào nút "Add"
        // history("/add-user"); // Thay đổi "/add-user" thành đường dẫn tương ứng của trang bạn muốn chuyển hướng
    };

    const handleEditClick = (user: any) => {
        // Chuyển hướng sang trang chỉnh sửa người dùng khi nhấn vào nút "Edit"
        // history(`/edit-user/${user.id}`); // Thay đổi "/edit-user/:id" thành đường dẫn tương ứng của trang bạn muốn chuyển hướng, và truyền id của user cần chỉnh sửa
    };

    const deleteUser = async (id: any) => {
        // Xóa người dùng và cập nhật dataSource (không cần chuyển hướng)
        // await sendDelete("deleteProduct", { id });
        // const newDataSource = dataSource.filter((item) => item.id !== id);
        // setDataSource(newDataSource);
    };

    const columns = [
        // Các cột của bảng
        {
            title: "Id",
            dataIndex: "id",
            key: "id",
        },
        {
            title: "UserName",
            dataIndex: "userName",
            key: "userName",
        },
        {
            title: "Password",
            dataIndex: "password",
            key: "password",
        },
        {
            title: "Note",
            dataIndex: "note",
            key: "note",
        },
        {
            title: "Radio",
            dataIndex: "radio",
            key: "radio",
        },
        {
            title: "Switch",
            dataIndex: "switch",
            key: "switch", // Đổi key từ "switch" thành "switchForm" để tránh xung đột với từ khóa "switch" của JavaScript
        },
        {
            title: "Remember Me",
            dataIndex: "remember",
            key: "rememberMe",
        },
        {
            title: "Select Option",
            dataIndex: "selectOption",
            key: "selectOption",
        },
        {
            title: "Action",
            key: "action",
            render: (text: string, record: any) => ( // Loại bỏ tham số 'text' không sử dụng
                <span>
                    <Button onClick={() => handleEditClick(record)}>Edit</Button>
                    <Button onClick={() => deleteUser(record.id)}>Delete</Button>
                </span>
            ),
        },
    ];

    return (
        <div>
            <Table dataSource={dataSource} columns={columns} />
            <Button className="btn-add" onClick={handleAddClick}>
                Add
            </Button>
        </div>
    );
};

export default AllUser;
