import React, { useEffect, useState } from "react";
import { Button, Table } from "antd";
import { useHistory } from "react-router-dom"; // Import useHistory từ react-router-dom

const AllUser = () => {
  const [dataSource, setDataSource] = useState([]);
  const history = useHistory(); // Khởi tạo useHistory

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    // Lấy dữ liệu từ API hoặc từ nguồn khác
  };

  const handleAddClick = () => {
    // Chuyển hướng sang trang khác khi nhấn vào nút "Add"
    history.push("/add-user"); // Thay đổi "/add-user" thành đường dẫn tương ứng của trang bạn muốn chuyển hướng
  };

  const handleEditClick = (user) => {
    // Chuyển hướng sang trang khác khi nhấn vào nút "Edit"
    history.push(`/edit-user/${user.id}`); // Thay đổi "/edit-user" thành đường dẫn tương ứng của trang bạn muốn chuyển hướng, và truyền id của user cần chỉnh sửa
  };

  const columns = [
    // Các cột của bảng
    {
      title: "Action",
      key: "action",
      render: (text, record) => (
        <span>
          <Button onClick={() => handleEditClick(record)}>Edit</Button>
          <Button onClick={() => deleteUser(record.id)}>Delete</Button>
        </span>
      ),
    },
  ];

  return (
    <div>
      <Table dataSource={dataSource} columns={columns} />
      <Button className="btn-add" onClick={handleAddClick}>
        Add
      </Button>
    </div>
  );
};

export default AllUser;
