import React, { useState } from 'react';
import { Form, Input, Checkbox, Switch, Radio, Button, Select, Typography } from 'antd';
import { UserOutlined, LockOutlined } from '@ant-design/icons';

const { Group: RadioGroup } = Radio; // Định nghĩa RadioGroup từ Radio

const ComponentForm: React.FC = () => {

  const inputText = [
    {
      value: 'username',
      type: 'text',
      placeHolder: 'Username',
      error: 'Please input your username!'
    },
    {
      value: 'password',
      type: 'password',
      placeHolder: 'Password',
      error: 'Please input your password!'
    },
    {
      value: 'textField',
      type: 'text',
      placeHolder: 'Text Field',
      error: 'Please input text field!'
    },
  ];

  const [rememberMe, setRememberMe] = useState(false);
  const [switchValue, setSwitchValue] = useState(false);
  const [radioValue, setRadioValue] = useState(1);

  const handleSwitchChange = (checked: boolean) => {
    setSwitchValue(checked);
  };

  const handleRadioChange = (e: any) => {
    setRadioValue(e.target.value);
  };

  const handleCancel = () => {
    // 
  };

  const handleNext = () => {
    // 
  };

  const option = [
    "Dropdown option",
    "Dropdown option 1",
    "Dropdown option 2",
    "Dropdown option 3",
    "Dropdown option 4",
    "Dropdown option 5",
  ];

  return (
    <Form>
      <Form.Item name="FormConponent">
      {inputText.map((item) => (
        <div key={item.value}>
          <Typography.Text strong>{item.title}</Typography.Text>
          <Form.Item
            name={item.value}
            rules={[
              {
                required: true,
                message: item.error,
              },
            ]}
          >
            <Input
              prefix={item.value === 'password' ? <LockOutlined /> : <UserOutlined />}
              type={item.type}
              placeholder={item.placeHolder}
            />
          </Form.Item>
        </div>
      ))}
      </Form.Item>
      <Form.Item>
        <Checkbox
          checked={rememberMe}
          onChange={e => setRememberMe(e.target.checked)}
        >
          Remember me
        </Checkbox>
      </Form.Item>
      <Form.Item>
        <Switch
          checked={switchValue}
          onChange={handleSwitchChange}
        />
      </Form.Item>
      <Form.Item>
        <RadioGroup
          onChange={handleRadioChange}
          value={radioValue}>
          <Radio value={1}>Button 1</Radio>
          <Radio value={2}>Button 2</Radio>
          <Radio value={3}>Button 3</Radio>
        </RadioGroup>
      </Form.Item>
      <Form.Item>
      <label className="dropdown-title">Dropdown Title</label>
          <Form.Item name={"selectBox"} initialValue={option[0]}>
            <Select className="input-text">
              {option.map((data, index) => {
                return (
                  <Select.Option key={index.toString()} value={data}>
                    {data}
                  </Select.Option>
                );
              })}
            </Select>
          </Form.Item>
      </Form.Item>
      <Form.Item>
        <Button onClick={handleCancel}> Cancel </Button>
        <Button type="primary" onClick={handleNext}> Next </Button>
      </Form.Item>
    </Form>
  );
};

export default ComponentForm;
