import React, { useState } from 'react';
import { Modal, Form, Input, Checkbox, Radio, Switch, Select, Button } from 'antd';
import { UserOutlined, LockOutlined } from '@ant-design/icons';
import { sendPost } from "../api";

const { Option } = Select;

const FormCRUD = (props: any) => {
  const { isOpen, setOpen, setDataSource } = props;
  const [statusSwitch, setStatusSwitch] = useState(false);

  const inputText = [
    {
      label: "Username",
      value: "username",
      type: "text",
      placeHolder: "Enter username",
      error: "You need to enter a username",
    },
    {
      label: "Password",
      value: "password",
      placeHolder: "Enter password",
      type: "password",
      note: "Your password must be between 4 and 12 characters",
      error: "You need to enter a password",
    },
    {
      label: "Input Text Label",
      value: "anything",
      type: "text",
      placeHolder: "Type here",
      error: "You need to enter a Text Label",
    },
  ];

  const radioInput = [
    { name: "Radio selection 1", value: "1" },
    { name: "Radio selection 2", value: "2" },
    { name: "Radio selection 3", value: "3" },
  ];

  const options = [
    "Dropdown option",
    "Dropdown option 1",
    "Dropdown option 2",
    "Dropdown option 3",
    "Dropdown option 4",
    "Dropdown option 5",
  ];

  const onFinish = async (values: any) => {
    const submitData = {
      userName: values.username,
      password: values.password,
      note: values.anything,
      rememberMe: values.checkbox,
      radio: values.radio,
      switchForm: statusSwitch ? 1 : 0,
      selectOption: values.selectBox,
    };
    const res = await sendPost(`saveProduct`, submitData);
    setDataSource((prevData: any) => [
      ...prevData,
      { id: res.id, ...submitData },
    ]);
    setOpen(false);
  };

  return (
    // <Modal
    //   className="model-custom"
    //   title={null}
    //   visible={isOpen}
    //   onCancel={() => setOpen(false)}
    //   footer={null}
    // >
      <Form onFinish={onFinish} className="form-container">
        {inputText.map((item) => (
          <Form.Item
            key={item.value}
            name={item.value}
            rules={[
              {
                required: true,
                message: item.error,
                min: item.value === "password" ? 4 : undefined,
                max: item.value === "password" ? 12 : undefined,
              },
            ]}
          >
            <Input
              prefix={item.value === "password" ? <LockOutlined /> : <UserOutlined />}
              type={item.type}
              placeholder={item.placeHolder}
            />
          </Form.Item>
        ))}

        <Form.Item name="checkbox" valuePropName="checked">
          <Checkbox>Remember Me</Checkbox>
        </Form.Item>

        <Form.Item name="radio" initialValue="0">
          <Radio.Group>
            {radioInput.map((item) => (
              <Radio key={item.value} value={item.value}>
                {item.name}
              </Radio>
            ))}
          </Radio.Group>
        </Form.Item>

        <Form.Item name="switch">
          <Switch onChange={() => setStatusSwitch(!statusSwitch)} />
          <span style={{ marginLeft: 8 }}>{statusSwitch ? "On" : "Off"}</span>
        </Form.Item>

        <Form.Item name="selectBox" initialValue={options[0]}>
          <Select>
            {options.map((option, index) => (
              <Option key={index} value={option}>
                {option}
              </Option>
            ))}
          </Select>
        </Form.Item>

        <div className="button-container">
          <Button className="btn-custom cancel" onClick={() => setOpen(false)}>
            Cancel
          </Button>
          <Button className="btn-custom next" htmlType="submit">
            Next
          </Button>
        </div>
      </Form>
    // </Modal>
  );
};

export default FormCRUD;
