import React, { useEffect, useState } from "react";
import { Button, Table } from "antd";
import { sendDelete, sendGet } from "../api";
import { useNavigate } from 'react-router-dom';

const AllUser = () => {
    const [dataSource, setDataSource] = useState<any[]>([]);
      const navigate = useNavigate(); // Khởi tạo useHistory

    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await sendGet('findAllEmployees');
                setDataSource(data);
            } catch (error) {
                // Exeption
            }
        };
        fetchData();
    }, []);

    const fetchData = async () => {
        // Lấy dữ liệu từ API hoặc từ nguồn khác
    };

    const handleAddClick = () => {
        // Chuyển hướng sang trang thêm người dùng khi nhấn vào nút "Add"
        navigate("/add-user"); // Thay đổi "/add-user" thành đường dẫn tương ứng của trang bạn muốn chuyển hướng
    };

    const handleEditClick = (user: any) => {
        // Chuyển hướng sang trang chỉnh sửa người dùng khi nhấn vào nút "Edit"
        navigate(`/edit-user/${user.id}`); // Thay đổi "/edit-user/:id" thành đường dẫn tương ứng của trang bạn muốn chuyển hướng, và truyền id của user cần chỉnh sửa
    };

    const deleteUser = async (id: BigInt) => {
        // Xóa người dùng và cập nhật dataSource (không cần chuyển hướng)
        await sendDelete(`deleteEmployees/${id}`)
        const newDataSource = dataSource.filter((item) => item.id !== id);
        setDataSource(newDataSource);
    };

    const columns = [
        // Các cột của bảng
        {
            title: "Id",
            dataIndex: "id",
            key: "id",
        },
        {
            title: "UserName",
            dataIndex: "userName",
            key: "userName",
        },
        {
            title: "Password",
            dataIndex: "password",
            key: "password",
        },
        {
            title: "Note",
            dataIndex: "note",
            key: "note",
        },
        {
            title: "Radio",
            dataIndex: "radioSelection",
            key: "Radio Selection",
        },
        {
            title: "Switch Mode",
            dataIndex: "switchMode",
            key: "switchMode",
            render: (switchValue: boolean) => (
              <span>{switchValue ? "On" : "Off"}</span>
            ),
          },
          {
            title: "Remember Me",
            dataIndex: "rememberMe",
            key: "rememberMe",
            render: (rememberValue: boolean) => (
              <span>{rememberValue ? "True" : "False"}</span>
            ),
          },
        {
            title: "Dropdown",
            dataIndex: "dropdownOption",
            key: "dropdownOption",
        },
        {
            title: "Action",
            key: "action",
            render: (text: any, record: any) => (
                <span>
                    <Button onClick={() => handleEditClick(record)}>Edit</Button>
                    <Button onClick={() => deleteUser(record.id)}>Delete</Button>
                </span>
            ),
        },
    ];

    return (
        <div>
            <Table dataSource={dataSource} columns={columns} />
            <Button className="btn-add" onClick={handleAddClick}>
                Add
            </Button>
        </div>
    );
};

export default AllUser;
