import React, { useEffect, useState } from "react";
import { Button, Table } from "antd";
import { sendDelete, sendGet } from "../../api";
import { useNavigate } from 'react-router-dom';
import "./style.css";

const AllUser = () => {
    const [dataSource, setDataSource] = useState<any[]>([]);
    const navigate = useNavigate();
    const [pagination, setPagination] = useState({current: 1, pageSize: 5});

    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await sendGet('findAllEmployees');
                setDataSource(data);
            } catch (error) {
                console.log(error);
            }
        };
        fetchData();
    }, []);


    const handleAddClick = () => {
        navigate(`/add`);
    };

    // Trong component AllUser
    const handleEditClick = (user: any) => {
        const { password, ...userWithoutPassword } = user;
        navigate(`/edit/${user.id}`, { state: { user: userWithoutPassword, isEdit: true } });
    };

    const deleteUser = async (id: BigInt) => {
        // Xóa người dùng và cập nhật dataSource (không cần chuyển hướng)
        await sendDelete(`deleteEmployees/${id}`)
        const newDataSource = dataSource.filter((item) => item.id !== id);
        setDataSource(newDataSource);
    };

    const handlePaginationChange = (page: any, pageSize: any) => {
        setPagination({ current: page, pageSize: pageSize });
    };
    

    const columns = [
        // Các cột của bảng
        {
            title: "Id",
            dataIndex: "id",
            key: "id",
        },
        {
            title: "UserName",
            dataIndex: "userName",
            key: "userName",
        },

        {
            title: "Note",
            dataIndex: "note",
            key: "note",
        },
        {
            title: "Remember Me",
            dataIndex: "remember",
            key: "rememberMe",
            render: (remember: boolean) => (
                <span>{remember ? "On" : "Off"}</span>
            ),
        },
        {
            title: "Switch Mode",
            dataIndex: "switchMode",
            key: "switchMode",
            render: (switchMode: boolean) => (
                <span>{switchMode ? "On" : "Off"}</span>
            ),
        },
        {
            title: "Radio",
            dataIndex: "radioSelection",
            key: "Radio Selection",
        },
        {
            title: "Dropdown",
            dataIndex: "dropdownOption",
            key: "dropdownOption",
        },
        {
            title: "Action",
            key: "action",
            render: (record: any) => (
                <span>
                    <Button className="btn-edit" onClick={() => handleEditClick(record)}>Edit</Button>
                    <Button className="btn-delete" onClick={() => deleteUser(record.id)}>Delete</Button>
                </span>
            ),
        },
    ];

    return (
        <div>
            <Button className="btn-add" onClick={handleAddClick}>
                Add
            </Button>
            <Table
                dataSource={dataSource}
                columns={columns}
                pagination={pagination}
                onChange={handlePaginationChange}
            >
            </Table>
        </div >
    );
};

export default AllUser;
